mapper.select:
select * from t_layer_jyz where isdel = '0'

	and mc like #{keyword}


	and zzjgdm = #{zzjgdm}

mapper.update:
update t_layer_jyz set mc = #{mc}, dz = #{dz},shape = #{shape},
x = #{x}, y = #{y}, lx = #{lx}, lxdh = #{lxdh}, fzr = #{fzr}, cjsj = sysdate,
zzjgdm =  #{zzjgdm}, zzjgmc = #{zzjgmc}, qydm = #{qydm} where tid = #{tid}
