mapper.select:
select * from t_layer_ld where isdel = '0'

	and mc like #{keyword}


	and zzjgdm = #{zzjgdm}

mapper.update:
update t_layer_ld set mc = #{mc}, dz = #{dz}, lxdh = #{lxdh},
x = #{x}, y = #{y}, fzr = #{fzr}, shape = #{shape}, createtime = sysdate,
zzjgdm = #{zzjgdm}, zzjgmc = #{zzjgmc}, qydm = #{qydm} where tid = #{tid}
