mapper.select:
select * from t_layer_sp where isdel = '0'

	and mc like #{keyword}


	and zzjgdm = #{zzjgdm}

mapper.update:
update t_layer_sp set mc = #{mc}, dz = #{dz}, bh = #{bh},
x = #{x}, y = #{y}, lx = #{lx}, shape = #{shape},createtime = sysdate,
zzjgdm =  #{zzjgdm}, zzjgmc = #{zzjgmc}, qydm = #{qydm} where tid = #{tid}
