mapper.select:
select * from t_layer_yy where isdel = '0'

	and mc like #{keyword}


	and zzjgdm = #{zzjgdm}

mapper.update:
update t_layer_yy set mc = #{mc}, dz = #{dz}, lxdh = #{lxdh}, fzr = #{fzr},
x = #{x}, y = #{y}, lx = #{lx}, shape = #{shape},createtime = sysdate,
zzjgdm =  #{zzjgdm}, zzjgmc = #{zzjgmc}, qydm = #{qydm} where tid = #{tid}
